package br.ufpi.capriovi.entidades.cadastros;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.PrimaryKeyJoinColumn;import javax.persistence.Table;@Entity@Table(name = "administrador")@PrimaryKeyJoinColumn(name = "UsuarioID")public class Administrador extends Usuario{	/**	 * 	 */	private static final long serialVersionUID = 571098318144671190L;	@Column(unique = true)	private String email;	public Administrador(){		setStatusAdmin(true);		setStatusUser(true);		setStatusGeral(true);		this.getPermissao().add("ROLE_ADMIN");			}	public String getEmail() {		return email;	}	public void setEmail(String email) {		this.email = email;	}}