package br.ufpi.capriovi.entidades.cadastros;import java.io.Serializable;import java.math.BigDecimal;import java.util.ArrayList;import java.util.Date;import java.util.List;import javax.persistence.CascadeType;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.OneToMany;import javax.persistence.Table;import javax.persistence.Temporal;import javax.persistence.TemporalType;import com.fasterxml.jackson.annotation.JsonIgnore;@Entity@Table(name = "fazenda")public class Fazenda implements Serializable {	private static final long serialVersionUID = -2422076034079719515L;	@Id	@GeneratedValue(strategy = GenerationType.IDENTITY)	private Long id;		private String nome;	private String municipio;	private String estado;	private String telefone;	private String cnpj;	private BigDecimal dimensao;	@Temporal(value = TemporalType.DATE)	private Date registro;			@ManyToOne(fetch = FetchType.EAGER, cascade = { CascadeType.MERGE, CascadeType.REFRESH })	@JoinColumn(name = "id_pecuarista")	private Pecuarista pecuarista;	@JsonIgnore	@OneToMany(mappedBy = "fazenda", cascade = CascadeType.REMOVE)		private List<Rebanho> rebanhos;		@JsonIgnore	@OneToMany(mappedBy = "fazenda", cascade = CascadeType.REMOVE)		private List<Funcionario> funcionarios;	public Fazenda() {		this.rebanhos = new ArrayList<Rebanho>();		this.funcionarios = new ArrayList<Funcionario>();	}		public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public String getNome() {		return nome;	}	public void setNome(String nome) {		this.nome = nome;	}	public String getMunicipio() {		return municipio;	}	public void setMunicipio(String municipio) {		this.municipio = municipio;	}	public String getEstado() {		return estado;	}	public void setEstado(String estado) {		this.estado = estado;	}	public String getTelefone() {		return telefone;	}	public void setTelefone(String telefone) {		this.telefone = telefone;	}	public String getCnpj() {		return cnpj;	}	public void setCnpj(String cnpj) {		this.cnpj = cnpj;	}	public BigDecimal getDimensao() {		return dimensao;	}	public void setDimensao(BigDecimal dimensao) {		this.dimensao = dimensao;	}	public Pecuarista getPecuarista() {		return pecuarista;	}	public void setPecuarista(Pecuarista pecuarista) {		this.pecuarista = pecuarista;	}	public List<Rebanho> getRebanhos() {		return rebanhos;	}	public void setRebanhos(List<Rebanho> rebanhos) {		this.rebanhos = rebanhos;	}		public Date getRegistro() {		return registro;	}	public void setRegistro(Date registro) {		this.registro = registro;	}	public List<Funcionario> getFuncionarios() {		return funcionarios;	}	public void setFuncionarios(List<Funcionario> funcionarios) {		this.funcionarios = funcionarios;	}	@Override	public int hashCode() {		final int prime = 31;		int result = 1;		result = prime * result + ((id == null) ? 0 : id.hashCode());		return result;	}	@Override	public boolean equals(Object obj) {		if (this == obj)			return true;		if (obj == null)			return false;		if (getClass() != obj.getClass())			return false;		Fazenda other = (Fazenda) obj;		if (id == null) {			if (other.id != null)				return false;		} else if (!id.equals(other.id))			return false;		return true;	}}