package br.ufpi.capriovi.entidades.cadastros;import javax.persistence.CascadeType;import javax.persistence.Entity;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.PrimaryKeyJoinColumn;import javax.persistence.Table;@Entity@Table(name = "funcionario")@PrimaryKeyJoinColumn(name = "UsuarioID")public class Funcionario extends Usuario {	/**	 * 	 */	private static final long serialVersionUID = -5726650574246292717L;	@ManyToOne(cascade = CascadeType.REFRESH)	@JoinColumn(name = "id_fazenda")	private Fazenda fazenda;	public Funcionario(){		setStatusAdmin(true);		setStatusUser(true);		setStatusGeral(true);		this.getPermissao().add("ROLE_FUNCIONARIO");				this.fazenda = new Fazenda();	}		public Fazenda getFazenda() {		return fazenda;	}	public void setFazenda(Fazenda fazenda) {		this.fazenda = fazenda;	}}