package br.ufpi.capriovi.entidades.cadastros;import java.util.HashSet;import java.util.Set;import javax.persistence.CascadeType;import javax.persistence.Entity;import javax.persistence.FetchType;import javax.persistence.OneToMany;import javax.persistence.PrimaryKeyJoinColumn;import javax.persistence.Table;import com.fasterxml.jackson.annotation.JsonIgnore;@Entity@Table(name = "pecuarista")@PrimaryKeyJoinColumn(name = "UsuarioID")public class Pecuarista extends Usuario {	/**	 * 	 */	private static final long serialVersionUID = -5543901069918194226L;	@JsonIgnore	@OneToMany(mappedBy = "pecuarista", fetch = FetchType.EAGER, cascade = CascadeType.REMOVE)	private Set<Fazenda> fazendas;		public Pecuarista(){		setStatusAdmin(false);		setStatusUser(false);		setStatusGeral(false);		this.getPermissao().add("ROLE_PECUARISTA");				fazendas = new HashSet<Fazenda>();	}	public Set<Fazenda> getFazendas() {		return fazendas;	}	public void setFazendas(Set<Fazenda> fazendas) {		this.fazendas = fazendas;	}}