package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;public enum IndiceFenotipicoEnum {	DEFAULT(0, "-"), 	TIPO_PESONASCER(1, "Peso ao Nascer"), 	 	TIPO_PESODESMAME(2, "Peso ao Desmame (4 meses)"),		TIPO_PERCGORDURA(3, "Percentual de Gordura em Leite"),	TIPO_PERCPROTEINA(4, "Percentual de Proteína"),	TIPO_CELSOMATICA(5, "Células Somáticas"),	TIPO_PERIESCROTAL(6, "Perímetro Escrotal"),	TIPO_CPM(7, "CPM"),	TIPO_ESPESGORDURA(8, "Espessura de Gordura"),	TIPO_AREAOLHOLOMBO(9, "Área de Olho de Lombo"),	TIPO_INTERGERACAO(10, "Intervalo de Geração"),	TIPO_DIFSELECAO(11, "Diferencial de Seleção"),	TIPO_HETEROZO(12, "Heterozo"),	TIPO_GANHOGENETICO(13, "Ganho Genético Esperado");	private Integer codigo;	private String descricao;	IndiceFenotipicoEnum(Integer codigo, String descricao) {		this.codigo = codigo;		this.descricao = descricao;	}		public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}		public static IndiceFenotipicoEnum getEnumByCodigo(Integer codigo) {		for (IndiceFenotipicoEnum  elemento : IndiceFenotipicoEnum.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}		public static Integer getCodigoByDesc(String desc) {		for (TipoMotivoSaidaEnum  elemento : TipoMotivoSaidaEnum.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}		public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (IndiceFenotipicoEnum x : IndiceFenotipicoEnum.values()) {			list.add(x.getDescricao());		}		return list;	}}