package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;public enum IntervaloCurvaCrescimento {	DEFAULT(0, "-"), 	INTERVALO_1MES(1, "1 Mes"), 	INTERVALO_6MESES(2,"6 Meses"), 	INTERVALO_1ANO(3, "1 Ano");	private Integer codigo;	private String descricao;	IntervaloCurvaCrescimento(Integer codigo, String descricao) {		this.codigo = codigo;		this.descricao = descricao;	}		public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}		public static IntervaloCurvaCrescimento getEnumByCodigo(Integer codigo) {		for (IntervaloCurvaCrescimento  elemento : IntervaloCurvaCrescimento.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}		public static Integer getCodigoByDesc(String desc) {		for (IntervaloCurvaCrescimento  elemento : IntervaloCurvaCrescimento.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}		public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (IntervaloCurvaCrescimento x : IntervaloCurvaCrescimento.values()) {			list.add(x.getDescricao());		}		return list;	}}