package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;/** * Tipos de criação de uma fazenda. */public enum TipoCorMucosaEnum {	DEFAULT(-1, "-"),	MUCOSA_PALIDA(1,"Palida"),	MUCOSA_EDEMACIADA(2,"Edemaciada"),	MUCOSA_NORMAL(3,"Normal");		private Integer codigo;	private String descricao;		TipoCorMucosaEnum(Integer codigo, String descricao){		this.codigo = codigo;		this.descricao = descricao;	}		public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}		public static TipoCorMucosaEnum getEnumByCodigo(Integer codigo) {		for (TipoCorMucosaEnum  elemento : TipoCorMucosaEnum.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}		public static Integer getCodigoByDesc(String desc) {		for (TipoCorMucosaEnum  elemento : TipoCorMucosaEnum.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}		public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (TipoCorMucosaEnum x : TipoCorMucosaEnum.values()) {			list.add(x.getDescricao());		}		return list;	}	}