package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;/** * Tipos de criação de uma fazenda. */public enum TipoCriacaoEnum {	DEFAULT(0, "-"),	CRIACAO_CAPRINO(1,"Caprinos"),	CRIACAO_OVINO(2,"Ovinos"),	CRIACAO_AMBOS(3,"Ambos");		private Integer codigo; 	private String descricao;		TipoCriacaoEnum(Integer codigo, String descricao){		this.codigo = codigo;		this.descricao = descricao;	}		public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}		public static TipoCriacaoEnum getEnumByCodigo(Integer codigo) {		for (TipoCriacaoEnum  elemento : TipoCriacaoEnum.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}		public static Integer getCodigoByDesc(String desc) {		for (TipoCriacaoEnum  elemento : TipoCriacaoEnum.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}		public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (TipoCriacaoEnum x : TipoCriacaoEnum.values()) {			list.add(x.getDescricao());		}		return list;	}	}