package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;public enum TipoDesenvolvimentoPonderal {	DEFAULT(0, "-"),	TIPO_AONASCER(1,"Ao nascer"),	TIPO_AODESMAME(2,"Desmame"),	TIPO_OUTROS(3,"Outros");		private Integer codigo;	private String descricao;		TipoDesenvolvimentoPonderal(Integer codigo, String descricao){		this.codigo = codigo;		this.descricao = descricao;	}		public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}		public static TipoDesenvolvimentoPonderal getEnumByCodigo(Integer codigo) {		for (TipoDesenvolvimentoPonderal  elemento : TipoDesenvolvimentoPonderal.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}		public static Integer getCodigoByDesc(String desc) {		for (TipoDesenvolvimentoPonderal  elemento : TipoDesenvolvimentoPonderal.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}		public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (TipoDesenvolvimentoPonderal x : TipoDesenvolvimentoPonderal.values()) {			list.add(x.getDescricao());		}		return list;	}	}