package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;/** * Tipos de criação de uma fazenda. */public enum TipoManejoEnum{	DEFAULT(0, "-"),	MANEJO_INTENSIVO(1,"Intensivo"),	MANEJO_EXTENSIVO(2,"Extensivo"),	MANEJO_SEMI(3,"Semi-Intensivo");		private Integer codigo;	private String descricao;		TipoManejoEnum(Integer codigo, String descricao){		this.codigo = codigo;		this.descricao = descricao;	}		public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}		public static TipoManejoEnum getEnumByCodigo(Integer codigo) {		for (TipoManejoEnum  elemento : TipoManejoEnum.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}		public static Integer getCodigoByDesc(String desc) {		for (TipoManejoEnum  elemento : TipoManejoEnum.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}		public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (TipoManejoEnum x : TipoManejoEnum.values()) {			list.add(x.getDescricao());		}		return list;	}}