package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;public enum TipoMotivoSaidaEnum {	DEFAULT(0, "-"),	MOTIVO_VENDA(1,"Venda"),	MOTIVO_MORTE(2,"Morte"),	MOTIVO_ROUBO(3,"Roubo"),	MOTIVO_ALIMENTACAO(4,"Alimentação"),	MOTIVO_EMPRESTIMO(5,"Emprestimo"),	MOTIVO_OUTROS(6,"Outros");	private Integer codigo;	private String descricao;	TipoMotivoSaidaEnum(Integer codigo, String descricao){		this.codigo = codigo;		this.descricao = descricao;	}	public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}	public static TipoMotivoSaidaEnum getEnumByCodigo(Integer codigo) {		for (TipoMotivoSaidaEnum  elemento : TipoMotivoSaidaEnum.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}	public static Integer getCodigoByDesc(String desc) {		for (TipoMotivoSaidaEnum  elemento : TipoMotivoSaidaEnum.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}	public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (TipoMotivoSaidaEnum x : TipoMotivoSaidaEnum.values()) {			list.add(x.getDescricao());		}		return list;	}}