package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;public enum TipoParicao {	DEFAULT(0, "-"), 	SIM(1, "Sim"), 	NAO(2, "NÃ£o"),	EMANDAMENTO(3, "Em Andamento");		private Integer codigo;	private String descricao;	TipoParicao(Integer codigo, String descricao) {		this.codigo = codigo;		this.descricao = descricao;	}		public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}		public static TipoParicao getEnumByCodigo(Integer codigo) {		for (TipoParicao  elemento : TipoParicao.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}		public static Integer getCodigoByDesc(String desc) {		for (TipoParicao  elemento : TipoParicao.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}		public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (TipoParicao x : TipoParicao.values()) {			list.add(x.getDescricao());		}		return list;	}	}