package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;/** * Tipos de criação de uma fazenda. */public enum TipoPartoEnum {	DEFAULT(0, "-"),	PARTO_SIMPLES(1,"Simples"),	PARTO_DUPLO(2,"Duplo"),	PARTO_TRIPLO(3,"Triplo"),	PARTO_QUADRUPLO(4,"Quádruplo");		private Integer codigo;	private String descricao;		TipoPartoEnum(Integer codigo, String descricao){		this.codigo = codigo;		this.descricao = descricao;	}		public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}		public static TipoPartoEnum getEnumByCodigo(Integer codigo) {		for (TipoPartoEnum  elemento : TipoPartoEnum.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}		public static Integer getCodigoByDesc(String desc) {		for (TipoPartoEnum  elemento : TipoPartoEnum.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}		public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (TipoPartoEnum x : TipoPartoEnum.values()) {			list.add(x.getDescricao());		}		return list;	}	}