package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;public enum TipoRelatorio {	DEFAULT(0, "-"), 	ENTRADA(1, "Animais por Entrada"), 	ZOOTECNICOS(2,"Dados Zootécnicos"), 	NUMEROCRIAS(3, "Número de Crias"), 	NUMEROCRIASMACHO(4,"Número de Crias Machos"), 	NUMEROPARTOS(6, "Número de Partos"),	NUMEROPARTOSDUPLO(7, "Número de Partos Duplos"),	ANIMAISEMALEITAMENTO(8, "Animais em Aleitamento"),	ANIMAISEMDESMAME(9, "Animais em Desmame"),	FEMEASIDADEREPRODUTIVA(10, "Fêmeas Jovens em Idade Reprodutiva"),	MATRIZSECA(11, "Matrizes secas"),	MATRIZLACTACAO(12, "Matrizes em Lactação"),	MATRIZLACTACAOGSTANTE(13, "Latrizes em Lactação Gestantes"),	MATRIZLACTACAONAOGSTANTE(14, "Latrizes em Lactação Não-Gestantes");		private Integer codigo;	private String descricao;	TipoRelatorio(Integer codigo, String descricao) {		this.codigo = codigo;		this.descricao = descricao;	}		public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}		public static TipoRelatorio getEnumByCodigo(Integer codigo) {		for (TipoRelatorio  elemento : TipoRelatorio.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}		public static Integer getCodigoByDesc(String desc) {		for (TipoRelatorio  elemento : TipoRelatorio.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}		public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (TipoRelatorio x : TipoRelatorio.values()) {			list.add(x.getDescricao());		}		return list;	}	}