package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;/** * Tipos de criação de uma fazenda. */public enum TipoSexoEnum {	DEFAULT(0, "-"),	SEXO_MACHO(1,"Macho"),	SEXO_FEMEA(2,"Fêmea");		private Integer codigo;	private String descricao;		TipoSexoEnum(Integer codigo, String descricao){		this.codigo = codigo;		this.descricao = descricao;	}		public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}		public static TipoSexoEnum getEnumByCodigo(Integer codigo) {		for (TipoSexoEnum  elemento : TipoSexoEnum.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}		public static Integer getCodigoByDesc(String desc) {		for (TipoSexoEnum  elemento : TipoSexoEnum.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}		public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (TipoSexoEnum x : TipoSexoEnum.values()) {			list.add(x.getDescricao());		}		return list;	}	}