package br.ufpi.capriovi.suporte.tiposEnum;import java.util.ArrayList;import java.util.List;/** * Tipos de criação de uma fazenda. */public enum TipoStatusEnum {	DEFAULT(0, "-"),	STATUS_ATIVO(1,"Ativo"),	STATUS_INATIVO(2,"Inativo");		private Integer codigo;	private String descricao;		TipoStatusEnum(Integer codigo, String descricao){		this.codigo = codigo;		this.descricao = descricao;	}		public Integer getCodigo() {		return codigo;	}	public void setCodigo(Integer codigo) {		this.codigo = codigo;	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}		public static TipoStatusEnum getEnumByCodigo(Integer codigo) {		for (TipoStatusEnum  elemento : TipoStatusEnum.values()) {			if(elemento.getCodigo().equals(codigo)) {				return elemento;			}		}		return null;	}		public static Integer getCodigoByDesc(String desc) {		for (TipoStatusEnum  elemento : TipoStatusEnum.values()) {			if(elemento.getDescricao().equals(desc)) {				return elemento.getCodigo();			}		}		return null;	}		public static List<String> getList() {		List<String> list = new ArrayList<String>();		for (TipoStatusEnum x : TipoStatusEnum.values()) {			list.add(x.getDescricao());		}		return list;	}}